/*
 * Инициализация
 */
jQuery(document).ready(function ()
{
	jQuery("div.menu-content").click(function (e)
	{
		if (e.target.nodeName.toLowerCase() != "img")
			return;

		jQuery(e.target).parent().
			toggleClass("opened").
			children("ul").toggle();
	});
});